INSERT INTO `ninety_question` VALUES ('497', 'High Capital Costs', 'raw', 'a:5:{s:8:\"question\";s:64:\"Do the costs of installation and operation provide an advantage?\";s:5:\"lower\";s:89:\"Low capital costs indicate low barriers to entry and many competitors entering the market\";s:6:\"higher\";s:164:\"Having access to significant funds for purchase of equipment and production of goods may put you ahead of the competition and potentially accelerate entry to market\";s:7:\"answers\";a:5:{i:0;a:2:{s:5:\"title\";s:4:\"None\";s:5:\"score\";i:1;}i:1;a:2:{s:5:\"title\";s:5:\"Minor\";s:5:\"score\";i:2;}i:2;a:2:{s:5:\"title\";s:4:\"Some\";s:5:\"score\";i:3;}i:3;a:2:{s:5:\"title\";s:4:\"High\";s:5:\"score\";i:4;}i:4;a:2:{s:5:\"title\";s:11:\"Significant\";s:5:\"score\";i:5;}}s:6:\"global\";i:2;}', '2017-09-10 13:07:09', null, 'true', '18', '1', null, '10030', '1006');
