INSERT INTO `ninety_question` VALUES ('10366', 'Question 6', 'risk', 'a:7:{s:8:\"question\";s:76:\"To what extent do external stakeholders pose challenges to project delivery?\";s:6:\"global\";s:1:\"1\";s:4:\"desc\";s:0:\"\";s:5:\"lower\";s:114:\"External stakeholders that pose no challenge to project delivery are low risk as they will not hinder the project.\";s:6:\"medium\";s:214:\"External stakeholders that pose somewhat of a challenge to project delivery pose a medium risk to success. Develop a communication and engagement plan to remove the challenges and align with the project objectives.\";s:6:\"higher\";s:223:\"External stakeholders that pose large challenges to project delivery increase the risk of project failure. Develop a detailed communication and engagement plan to remove the challenges and possibly aligned with the project.\";s:7:\"answers\";a:3:{i:0;a:4:{s:5:\"alias\";s:4:\"none\";s:5:\"title\";s:4:\"None\";s:5:\"score\";s:1:\"0\";s:4:\"desc\";s:0:\"\";}i:1;a:4:{s:5:\"alias\";s:8:\"somewhat\";s:5:\"title\";s:8:\"Somewhat\";s:5:\"score\";s:1:\"1\";s:4:\"desc\";s:0:\"\";}i:2;a:4:{s:5:\"alias\";s:11:\"significant\";s:5:\"title\";s:11:\"Significant\";s:5:\"score\";s:1:\"2\";s:4:\"desc\";s:0:\"\";}}}', '2017-08-17 01:32:16', '2017-09-20 21:23:30', 'true', '88', '52', 'to-what-extent-is-the-level-of-interest-of-your-internal-stakeholders', '10018', '1005');
